#include <cstdio>  
#include <cstring>  
#include <cmath>  
#define PI acos(-1.0)  

struct Matrix {  
    double mat[3][3];  
    Matrix() {  
        memset(mat, 0, sizeof(mat));  
        for(int i = 0; i < 3; i++)  
            mat[i][i] = 1;  
    }  

    Matrix Multi(Matrix A, Matrix B) {  
        Matrix res;  
        for(int i = 0; i < 3; i++) {  
            for(int j = 0; j < 3; j++) {  
                res.mat[i][j] = 0;  
                for(int k = 0; k < 3; k++) {  
                    res.mat[i][j] = res.mat[i][j] + A.mat[i][k] * B.mat[k][j];  
                }  
            }  
        }  
        return res;  
    }  

    Matrix Translation(Matrix A, double p, double q) { 
        Matrix res;  
        res.mat[0][2] = p;  
        res.mat[1][2] = q;  
        return Multi(res, A);  
    }  

    Matrix Scale(Matrix A, double p) { 
        Matrix res;  
        res.mat[0][0] = res.mat[1][1]  = p;  
        return Multi(res, A);  
    }  

    Matrix Turn_UD(Matrix A) {  
        Matrix res;  
        res.mat[1][1] = -1;  
        return Multi(res, A);  
    }  

    Matrix Turn_LR(Matrix A) {
        Matrix res;  
        res.mat[0][0] = -1;  
        return Multi(res, A);  
    }  

    Matrix Rotate(Matrix A, double angle) {
        double rad = angle / 180.0 * PI;  
        Matrix res;  
        res.mat[0][0] = cos(rad); res.mat[0][1] = -sin(rad);  
        res.mat[1][0] = sin(rad); res.mat[1][1] = cos(rad);  
        return Multi(res, A);  
    }  
};  

struct Point {  
    double x, y;  
} P[10005];  

int main() {  
    int n, m;  
    char op[5];  
