    scanf("%d%d", &n, &m);  
    for(int i = 0; i < n; i++)  
        scanf("%lf%lf", &P[i].x, &P[i].y);  
    Matrix A;  
    for(int i = 0; i < m; i++) {  
        scanf("%s", op);  
        if(op[0] == 'X') A = A.Turn_UD(A);  
        else if(op[0] == 'Y') A = A.Turn_LR(A);  
        else if(op[0] == 'M') {  
            scanf("%lf%lf", &x, &y);  
            A = A.Translation(A, x, y);  
        }  
        else if(op[0] == 'S') {  
            scanf("%lf", &x);  
            A = A.Scale(A, x);  
        }  
        else if(op[0] == 'R') {  
            scanf("%lf", &x);  
            A = A.Rotate(A, x);  
        }  
    }  
    for(int i = 0; i < n; i++) {  
