#include <iostream>  
using namespace std;  
int M;  
const long long N = 32*2;  
long long t,b,c,f1,f2;  
struct tree   
{  
    long long line,cal;  
    long long a[N+1][N+1];  
};  
struct Node  
{  
    long long line,cal;  
    long long a[N+1][N+1];  
    Node(tree x)  
    {  
        line=x.line*2;  
        cal=2*x.cal;  
        for(int i=0;i<x.line*2;i++)  
        {  
            for(int j=0;j<x.cal;j++)  
            {  
                a[i][j]=x.a[i%x.line][j];  
            }  
        }  
        for(int i=0;i<x.line;i++)  
            for(int j=x.cal;j<x.cal*2;j++)  
                a[i][j]=0;  
        for(int i=x.line;i<2*x.line;i++){  
            for(int j=x.cal;j<2*x.cal;j++){  
                if(i==j)  
                    a[i][j]=1;  
                else  
                    a[i][j]=0;  
            }  
        }  
    }  
};  

Node isit(Node x,long long c)  
{  
    for(long long i=0;i<N;i++)  
        for(long long j=0;j<N;j++)  
            x.a[i][j]=c;  
    return x;  
}  

Node Matlab(Node x,Node s)  
{  
    Node ans(x);  
    ans.line = x.line,ans.cal = s.cal;  
    ans=isit(ans,0);  
    for(long long i=0;i<x.line;i++)  
    {  
        for(long long j=0;j<x.cal;j++)  
        {  
            for(long long k=0;k<s.cal;k++)  
            {  
                ans.a[i][j] += x.a[i][k]*s.a[k][j];  
                ans.a[i][j]=(ans.a[i][j])%M;  
            }  
        }  
    }  
    return ans;  
}  
Node Fast_Matrax(tree x,long long n)  
{  
    Node ans(x),tmp(x);  
    for(int i=0;i<ans.line/2;i++) 
    {  
        for(int j=0;j<ans.cal;j++)  
        {  
            ans.a[i][j]=ans.a[i+ans.line/2][j];  
        }  
    }  
    ans.line/=2;  
    while(n>0)  
    {  
        if(n%2)  
        {  
            ans=Matlab(ans,tmp);  
        }  
        tmp=Matlab(tmp,tmp);  
        n/=2;  
    }  
    return ans;  
}  
int main()  
{  
    int n,k,m;  
    while(~scanf("%d%d%d",&n,&k,&m))  
    {  
        M=m;  
        tree p;  
        p.line=n,p.cal=n;  
        for(int i=0;i<n;i++)  
            for(int j=0;j<n;j++)  
                scanf("%d",&p.a[i][j]);  
        Node ans=Fast_Matrax(p,k-1);  
        for(int i=0;i<ans.line;i++)  
        {  
            for(int j=0;j<ans.cal/2;j++)  
                printf("%d ",ans.a[i][j]);  
            puts("");  
        }  
    }  
    return 0;  
}

