using namespace std;  
#define LL long long  
const int N = 105;  
const LL mod = 1000007;  
struct Matrix  
{  
    int n;  
    LL a[N][N];  
    Matrix(){  
        memset(a, 0, sizeof(a));  
    }  
} ans, A;  
int node[N], n;  
int m, p;  

bool Judge(int x, int y)  
{  
    if(x - n == y) return true;  
    else if(x + n == y) return true;  
    else if(x - 1 == y && y % n != 0) return true;  
    else if(x + 1 == y && x % n != 0) return true;  
    else return false;  
}  

Matrix operator * (Matrix a, Matrix b)  
{  
    Matrix tmpans;  
    tmpans.n = a.n;  
    for(int i = 1; i <= a.n; i ++){  
        for(int j = 1; j <= a.n; j ++){  
            for(int k = 1; k <= a.n; k ++)  
            tmpans.a[i][j] = (tmpans.a[i][j] + a.a[i][k] * b.a[k][j]) % mod;   
        }  
    }  
    return tmpans;  
}  

void power(int k)  
{  
    while(k){  
        if(k & 1) ans = ans * A;  
        A = A * A;  
        k = k >> 1;  
    }  
}  

int main()  
{  
    int T;  
    cin >> T;  
    while(T --){  

        cin >> n >> m >> p;  
        for(int i = 0; i < m; i ++){  
            for(int j = 1; j <= n; j ++){  
                cin >> node[i * n + j];  
            }  
        }  
        for(int i = 1; i <= m * n; i ++){  
            for(int j = 1; j <= m * n; j ++){  
                A.a[i][j] = 0; ans.a[i][j] = 0;  
                if(i == j) continue;  
                if(node[i] == node[j]) A.a[i][j] = 1;  
                else if(Judge(i, j)){  
                    A.a[i][j] = 1;  
                }  
            }  
            ans.a[i][i] = 1;  
        }  

        ans.n = n * m; A.n = n * m;  
        power(p);  
        printf("%lld\n", ans.a[1][n * m] % mod);  
    }  
    return 0;  
}

