        res += (int)((str[i] - '0') * pow(10.0, len - 1 - i));  
    while(res)  
    {  
        if(res % 26 != 0)  
        {  
            ans[top++] = res % 26 + 'A' - 1;  
            res /= 26;  
        }  
        else  
        {  
            ans[top++] = 'Z';  
            res = res / 26 - 1;  
        }  

    }  
    for(int i = top - 1; i >= 0; --i)  
        printf("%c", ans[i]);  
    printf("\n");  
}  

