int map[150][150],mmax,mmin,flag; 
int n,sx[]={1,-1,0,0},zy[]={0,0,1,-1}; 
int loop[150][150]; 
void init() 
{ 
    int i,j; 
    mmax=-1; 
    mmin=999999; 
    memset(map,-1,sizeof(map)); 
    for(i=1;i<=n;i++) 
      for(j=1;j<=n;j++) 
        { 
            scanf("%d",&map[i][j]); 
            mmax= max(mmax,map[i][j]); 
            mmin= min(mmin,map[i][j]); 
        } 
} 
void dfs(int x,int y,int L,int R) 
{ 
    if(flag)  return; 
    if(x==n&&y==n) {flag=1;return;} 
    int i,j; 
    for(i=0;i<4;i++) 
    { 
        int xx=x+sx[i]; 
        int xy=y+zy[i]; 
        if(map[xx][xy]>=L&&map[xx][xy]<=R&&loop[xx][xy]==0) 
          {loop[xx][xy]=1;dfs(xx,xy,L,R);} 
    } 

} 
bool find(int k) 
{ 
    int i,j; 
    for(i=mmin;i<=mmax-k;i++) 
    { 
        flag=0; 
        if(map[1][1]<i||map[1][1]>i+k) continue; 
        if(map[n][n]<i||map[n][n]>i+k) continue; 
        memset(loop,0,sizeof(loop)); 
        loop[1][1]=1; 
        dfs(1,1,i,i+k); 
        if(flag) 
          return true; 
    } 
    return false; 
} 
int find_answer() 
{ 
    int i,j,x=0,y=mmax-mmin; 
    while(x<y) 
    { 
        int mid=(x+y)/2; 
        if(find(mid)) 
        { 
            y=mid; 
        } 
        else 
           x=mid+1; 
    } 
    return y; 
} 
