#define Max_V 8100  
#define inf 0x3f3f3f3f  
using namespace std;  
int n,m,k,w;  
struct Edge{  
    int x,y,z;  
};  
vector<Edge> g;  
bool have[Max_V];  
int maxPass[Max_V];
int ik[Max_V];
int p[Max_V];
int findMax(int a,int b)  
{  
    return a>b?a:b;  
}  
int findMin(int a,int b)  
{  
    return a>b?b:a;  
}  
void init()  
{  
    g.clear();  
    memset(have,0,sizeof(have));  
}  
void add_edge(int x,int y,int z)  
{
    g.push_back((Edge){x,y,z});  
    g.push_back((Edge){y,x,z});  
}  
int cmp(const void *a,const void *b)  
{  
    return *(int *)a-*(int *)b;  
}  
int find_way()  
{ 
    memset(maxPass,0,sizeof(maxPass));  
    maxPass[1]=inf;  
    bool flag=true;  
    int u,v,z;  
    while(flag)  
    {  
        flag=false;
        for(int i=0;i<g.size();i++)  
        {  
            u=g[i].x;v=g[i].y;z=g[i].z;  
            int t=maxPass[v];  
            maxPass[v]=findMax(maxPass[v],findMin(maxPass[u],z));  
            if (maxPass[v]!=t)
                flag=true;  
        }  
    }  
    int count=0;  
    for(int j=1;j<=n;j++)  
        if(have[j]&&maxPass[j]>=w) 
            ik[count++]=maxPass[j];  
    return count;  
}  
int solve()  
{  
    int mk=find_way();  
    p[0]=1;  
    qsort(ik,mk,sizeof(int),cmp);
    for(int i=1;i<mk;i++)  
    {  
        if(ik[i]>=p[i-1]*w+w)  
            p[i]=p[i-1]+1;  
        else p[i]=p[i-1];  
    }  
    return p[mk-1];  
}  
int main()  
{  
    int i;  
    while(~scanf("%d%d%d%d",&n,&m,&k,&w))  
    {  
        init();  
        int loc;  
        for(i=0;i<k;i++)  
        {  
            scanf("%d",&loc);  
            have[loc]=1;  
        }  
        int x,y,z;  
        for(i=0;i<m;i++)  
        {  
            scanf("%d%d%d",&x,&y,&z);  
            add_edge(x,y,z);  
        }  
        printf("%d\n",solve());  
    }  
    return 0;  
}

