int merge(int a[],int low,int mid,int high)  
{  
    int i=low,j=mid+1,k=low;  
    int count=0;
    while((i<=mid)&&(j<=high)) 
    {  
        if(a[i]<=a[j])  
        {  
            b[k++]=a[i++];  
        }  
        else  
        {  
            b[k++]=a[j++];  
            count+=(mid-i+1);  
        }  
    }  
    while(i<=mid)
    {  
        b[k++]=a[i++];  
    }  
    while(j<=high)  
    {  
        b[k++]=a[j++];  

    }  
    for(i=low;i<=high;++i)
    {  
        a[i]=b[i];  
    }  
    return count;  
}  
int sort(int a[],int low,int high)  
{  
    int x,y,z;  
    int mid=(high+low)/2;  
    int i=low,j=mid+1;  
    if(low>=high)  
    {  
        return 0;  
    }  
    x=sort(a,low,mid);  
    y=sort(a,mid+1,high);  
    z=merge(a,low,mid,high);  
    return (x+y+z);  
}  
