using namespace std;  
#define M 500  
#define inf 0x3f3f3f3f  
struct node{  
    int v,w,next;  
}mp[M*4];  
int head[M],dis[M],n,m,cnt;  
void add(int u,int v,int w){  
    mp[cnt].v=v;  
    mp[cnt].w=w;  
    mp[cnt].next=head[u];  
    head[u]=cnt++;  
}  
int bfs(){  
    memset(dis,-1,sizeof(dis));  
    queue<int> Q;  
    while(!Q.empty()) Q.pop();  
    dis[1]=0;  
    Q.push(1);  
    while(!Q.empty()){  
        int u=Q.front();  
        Q.pop();  
        for(int i=head[u];i!=-1;i=mp[i].next){  
            int v=mp[i].v;  
            if(dis[v]==-1 && mp[i].w>0){  
                dis[v]=dis[u]+1;  
                if(v==m) return 1;  
                Q.push(v);  
            }  
        }  
    }  
    return 0;  
}  
int dinic(int s,int flow){  
    if(s==m || flow==0) return flow;  
    int a,i,ans=flow;  
    for(i=head[s];i!=-1;i=mp[i].next){  
        int v=mp[i].v;  
        if(mp[i].w && dis[v]==dis[s]+1 && (a=dinic(v,min(ans,mp[i].w)))){  
            mp[i].w-=a;  
            mp[i^1].w+=a;  
            ans-=a;  
            if(ans==0) return flow;  
        }  
    }  
    return flow-ans;  
}  
int main(){  
    int sum,i,a,b,c;  
    while(scanf("%d%d",&n,&m)!=EOF){  
        sum=cnt=0;  
        memset(head,-1,sizeof(head));  
        for(i=0;i<n;i++){  
            scanf("%d%d%d",&a,&b,&c);  
            add(a,b,c);  
            add(b,a,0);  
            sum+=c;  
        }  
        int ans=0;  
        while(bfs()){  
            ans+=dinic(1,inf);  
        }  
        printf("%d\n",ans);  
    }  
    return 0;  
}

