void get_next(int cur, int sum, int a[])
{
    int t;
    if(cur == n)
        return;
    t = (int)fabs(total - sum - sum);
    if(t < min)
        min = t;
    get_next(cur + 1, sum, a);
    get_next(cur + 1, sum + a[cur], a);
}
