using namespace std;  

const int N = 65540;  
int prim[N];  

void Fast_Prim() 
{  
    int limit = sqrt(N * 1.0);  
    int num = 0;  
    memset(prim, 0, sizeof(prim));  
    for(int i = 2; i < limit; ++i)  
        if(!prim[i])  
            for(int j = i * i; j < N; j += i)  
                prim[j] = true;  
    for(int i = 2; i < N; ++i)  
        if(!prim[i])  
            prim[num++] = i;  
}  

int Prim_Induce(int n) 
{  
    int num, sum = n;  
    for(int i = 0; prim[i] * prim[i] <= n; ++i)  
    {  
        if(n % prim[i] == 0)  
        {  
            sum = sum / prim[i] * (prim[i] - 1);  
            while(n % prim[i] == 0)  
                n /= prim[i];  
        }  
    }  
    if(n != 1)  
        sum = sum / n * (n - 1);  
    return sum;  
}  

int main()  
{  
    Fast_Prim();  
    int n;  
    while(~scanf("%d", &n))  
        printf("%d\n", Prim_Induce(n));  
    return 0;  
}

