void Merge(int l,int r)
{
    int mid,x,y,i,j,k;
    double m;
    if (r-l+1<=6)
    {
        for (i=l;i<=r;i++)
        {
            for (j=i+1;j<=r;j++)
            {
                m=Dist(a[i],a[j]);
                if (m>ret/2) continue;
                for (k=j+1;k<=r;k++)
                {
                    ret=min(ret,m+Dist(a[j],a[k])+Dist(a[i],a[k]));
                }
            }
        }
        return;
    }
    mid=(l+r)/2;
    Merge(l,mid);
    Merge(mid+1,r);
    int up=0;
    for (i=l;i<=r;i++)
    {
        if (fabs(a[mid].x-a[i].x)<ret/2) tag[up++]=a[i];
