                memset(sum, 0, sizeof(sum));
                flag = 1;
                for(int i = 1; i <= n; i++)
                {
                    if(a[i] == n + 2)
                    {
                        flag = 0;continue;
                    }
                    sum[a[i]]++;
                    k[a[i]] = i;
                    if(sum[a[i]] > 1){flag = 0; break;}
                }
                if(flag == 1)
                {
                    printf("Sorted sequence determined after %d relations: ", i);
                    for(int i = 1; i <= n; i++)
                        printf("%c", k[i] + 'A' - 1);
                    printf(".\n");
                    break;
                }
            }
        }
        if(flag == 0)
            printf("Sorted sequence cannot be determined.\n");
    }
    return 0;
}

