#include <cstdio>  
#include <cstring>  
#include <queue>  
#include <algorithm>  
using namespace std;  
int n, m, h;  
char Map[31][31][31];   
int sx, sy, sz;
bool vis[31][31][31];  
struct Node  
{  
    int x, y, z, step;  
    friend bool operator < (Node a, Node b)  
    {  
        return a.step > b.step;  
    }  
};  
void getMap()  
{  
    for(int i = 0; i < n; i++)  
    {  
        for(int j = 0; j < m; j++)  
        {  
            scanf("%s", Map[i][j]);  
            for(int k = 0; k < h; k++)  
            {  
                if(Map[i][j][k] == 'S')  
                sx = i, sy = j, sz = k;  
            }  
        }   
        getchar();
    }  
}  
bool judge(int x, int y, int z)  
{  
    return x >= 0 && x < n && y >= 0 && y < m && z >= 0 && z < h && Map[x][y][z] != '#' && !vis[x][y][z];  
}  
void BFS(int x, int y, int z)  
{  
    priority_queue<Node> Q;  
    Node now, next;  
    memset(vis, false, sizeof(vis));  
    int move[6][3] = {0,0,1, 0,0,-1, 1,0,0, -1,0,0, 0,1,0, 0,-1,0};  
    now.x = x, now.y = y, now.z = z, now.step = 0;  
    vis[now.x][now.y][now.z] = true;  
    Q.push(now);  
    while(!Q.empty())  
    {  
        now = Q.top();  
        Q.pop();  
        if(Map[now.x][now.y][now.z] == 'E')  
        {  
            printf("Escaped in %d minute(s).\n", now.step);  
            return ;  
        }  
        for(int k = 0; k < 6; k++)  
        {  
            next.x = now.x + move[k][0];  
            next.y = now.y + move[k][1];  
            next.z = now.z + move[k][2];  
            next.step = now.step + 1;  
            if(judge(next.x, next.y, next.z))  
            {  
                Q.push(next);  
                vis[next.x][next.y][next.z] = true;  
            }   
        }   
    }  
    printf("Trapped!\n");   
}  
int main()  
{  
    while(scanf("%d%d%d", &n, &m, &h), n||m||h)  
    {  
        getMap();  
        BFS(sx, sy, sz);  
    }  
    return 0;  
}

