                if(s[i]=='(')  
                {  
                    if(flag==1) { s1.push('l');flag=0;}  
                    else if(flag==2)  { s1.push('s');flag=0;}  
                    else s1.push(s[i]);  
                }  
                if(s[i]=='*') s1.push(s[i]);  
                if(s[i]==')')  
                {  
                    double a1,a2;  
                    while(1)  
                    {  
                        char x=s1.top(); s1.pop();  
                        if(x=='(') break;  
                        a1=s2.top(); s2.pop();  
                        if(x=='l')  
                        {  
                            k=log(a1)/log(2);  
                            s2.push(k);  
                            break;  
                        }  
                        if(x=='s')  
                        {  
                            k=sqrt(a1);  
                            s2.push(k);  
                            break;  
                        }  
                        if(x=='*')  
                        {  
                            a2=s2.top();s2.pop();  
                            k=a1*a2;  
                            s2.push(k);  
                        }  
                    }  
                }  
            }  
            double t=s2.top();  
            if(t>100000000) puts("TLE");  
            else printf("%.2lf\n",t);  
        }  
    }  
    return 0;  
}

