void dfs(int cur)  
{  
    if(visit[cur]) return;  
    int size=vec[cur].size();  
    if(size==0)  
    {  
        dp[cur]=v[cur];  
        return;  
    }  
    for(int i=0;i<size;i++)  
    {  
        int child=vec[cur][i];  
        dfs(child);  
        visit[child]=1;  
        dp[cur]=Max(dp[cur],dp[child]+v[cur]);  
    }  
}  

