#define N 10000
#define LEN 32
#define X 10010

struct Trie
{
	int end;
	int v;
	struct Trie *son[26];
};

int n, nx;
int buf_cnt;
struct Trie *root, buf[N*LEN];
int max[X];
char x[X];

struct Trie* New_node()
{
	struct Trie *tmp = &buf[buf_cnt++];
	tmp->end = 0;
	tmp->v = 0;
	for (int i = 0; i < 26; i++)
		tmp->son[i] = 0;
	return tmp;
}

void insert(char *s, int v)
{
	struct Trie *cur = root;
	for (int i = 0; s[i] != 0; i++)
	{
		int id = s[i] - 'a';
		if (cur->son[id] == NULL)
			cur->son[id] = New_node();
		cur = cur->son[id];
	}
	cur->end = 1;
	cur->v = v;
}

void reversal(char *s)
{
	int i = 0;
	int j = strlen(s);
	j--;
	while (i < j)
	{
		char tmp = s[i];
		s[i] = s[j];
		s[j] = tmp;
		i++;
		j--;
	}
}

void query(int i)
{
	struct Trie *cur = root;
	for (int j = i; x[j] != 0; j--)
	{
		int id = x[j] - 'a';
		if (cur->son[id])
		{
			if (cur->son[id]->end)
			{
				if (max[j-1] != -1)
				{
					if (max[j-1]+cur->son[id]->v > max[i])
						max[i] = max[j-1] + cur->son[id]->v;
				}
			}
			cur = cur->son[id];
		}
		else
			break;
	}
}

int main()
{
	x[0] = 0;
	while (scanf("%d %s", &n, x+1) != EOF)
	{
		nx = strlen(x+1);
		buf_cnt = 0;
		root = New_node();
		for (int i = 0; i < n; i++)
		{
			char s[LEN];
			int v;
			scanf("%s %d", s, &v);
			reversal(s);
			insert(s, v);
		}
		max[0] = 0;
		for (int i = 1; i <= nx; i++)
		{
			max[i] = -1;
			query(i);
		}
		printf("%d\n", max[nx]);
	}
	return 0;
}

