{
	return DIS(a.x,a.y,fx,fy)<DIS(b.x,b.y,fx,fy);
}
double DP(int sta)
{
	if(dp[sta]!=0x7fffffff)
		return dp[sta];
	if(sta==0) {dp[0]=0.0;}
	else
	{
		int tem=sta,i=0;
		for(i=0;i<20;i++)
		{
			if((1<<i)&sta)break;
		}
		for(int j=i+1;j<2*n;j++)
		{
			if((sta&(1<<j))==0)continue;
			dp[sta]=min(DP(sta-(1<<j)-(1<<i))+dis[i][j],dp[sta]);
		}
	}
	return dp[sta];
}
