 return 1;
}
int cmpp(char *a, char *b) 
{
 int len_a = strlen(a), len_b = strlen(b), len = min(len_a, len_b), i;
 if(len_a != len_b) 
 {
 if(len_a > len_b) return 1;
 else return -1;
 }
 else 
 {
 for(i = 0; i < len; i++)
 {
 if(a[i] > b[i]) return 1; 
 if(a[i] < b[i]) return -1; 
 }
 return 0; 
 }
}
int to_int(char *s, int high, int low) 
{
 int ret = 0;
 for(int i = high; i <= low; i++) ret = ret*10 + s[i]-'0';
 return ret;
}
