                  scanf("%lf%lf",&P[i].node[j].x,&P[i].node[j].y);
         }
     if(n<=2){ printf("Case %d: %d\n",cases++,n);continue;}
     Point p;
     int max=0;
         for(i=1;i<=n;i++)
         {
             for(j=i+1;j<=n;j++)
             {
                 for(k=1;k<=P[i].num;k++)
                 {
                     for(m=1;m<=P[j].num;m++)
                     {   
                         ans=2;                   
                         Line a=Turn(P[i].node[k],P[j].node[m]) ;
                         for(x=1;x<=n;x++)
                         {
                             if(x==i||x==j) continue;
                             for(y=1;y<P[x].num;y++)
                             {
                                 Line b=Turn(P[x].node[y],P[x].node[y+1]);
                                 if(Line_Inst(a,b,p)&&dotOnSeg(p,P[x].node[y],P[x].node[y+1]))    
                                 {
