    scanf("%d",&ncase);
    for(t=1;t<=ncase;t++)
    {
        scanf("%d%d%d",&n,&m,&MP);
        MP*=4;
        memset(map,0,sizeof(map)); 
        for(i=0;i<n;i++)
        for(j=0;j<m;j++)
        { 
            scanf("%d",&ans); 
            for(k=9;k>=0;k--)
            if(ans>=a[k])
            {
                map[i][j][k]=1;
                ans-=a[k]; 
            }  
        } 
        scanf("%d%d%d%d",&x0,&y0,&endx,&endy); 
        memset(visit,0,sizeof(visit)); 
        printf("Case %d: %d\n",t,bfs()); 
    } 
    return 0; 
}

