void Link(int r,int c)
{
    S[c]++;
    C[size]=c;
    ROW[size]=r;
    U[size]=U[c];D[U[c]]=size;
    D[size]=c;U[c]=size;
    if(H[r]==-1) H[r]=L[size]=R[size]=size;
    else
    {
        L[size]=L[H[r]];R[L[H[r]]]=size;
        R[size]=H[r];L[H[r]]=size;
    }
    size++;
}
void remove(int c)
{
    int i;
    for(i=D[c];i!=c;i=D[i])
    {
        L[R[i]]=L[i];
        R[L[i]]=R[i];
    }
}
void resume(int c)
{
    int i;
    for(i=U[c];i!=c;i=U[i])
        L[R[i]]=R[L[i]]=i;
}
int h()
{
    int i,j,k,count=0;
    bool hash[cc];
    memset(hash,0,sizeof(hash));
    for(i=R[0];i;i=R[i])
    {
        if(hash[i]) continue;
        hash[i]=1;
        count++;
        for(j=D[i];j!=i;j=D[j])
            for(k=R[j];k!=j;k=R[k])
                hash[C[k]]=1;
    }
    return count;
}
void Dance(int k)
{
    int j,i,c,Min;
    if(k+h()>=ak) return;
    if(!R[0]) 
    {
        if(k<ak) ak=k;
        return;
    }
    for(Min=rr,i=R[0];i;i=R[i])
        if(Min>S[i]) Min=S[i],c=i;
    for(i=D[c];i!=c;i=D[i])
    {
        if(visit[mark[ROW[i]]]) continue;
        visit[mark[ROW[i]]]=1;
        remove(i);
        for(j=R[i];j!=i;j=R[j])
            remove(j);
        Dance(k+1);
        for(j=L[i];j!=i;j=L[j])
            resume(j);
        resume(i);
        visit[mark[ROW[i]]]=0;
    }
}
