#include <stdio.h>  
#include <string.h>  
#include <ctype.h>  
#include <stack>  
#define N 1050  

using namespace std;  

char s[N];  
stack<double>Num;         
stack<char>Ope;               

double JieXi();  
double JiSuan();  

void To_Prefix(){           
    char n[N * 3] = {"\0"};              
    int len = strlen(s), ni = 0;          

    for(int i = len - 2; i >= 0; i --){          
        if(isdigit(s[i]) || s[i] == '.'){         
            n[ni ++] = s[i];  
        }  
        else{  
            if(ni != 0 && n[ni - 1] != ' '){    
                n[ni ++] = ' ';  
            }  
            switch(s[i]){  
                case '+':                       
                case '-':  
                    while(!Ope.empty() && (Ope.top() ==  '*' || Ope.top() == '/')){  
                        n[ni ++] = Ope.top();  
                        n[ni ++] = ' ';  
                        Ope.pop();  
                    }  
                    break;  
                case '*':                        
                case '/':  
                case ')':  
                    break;  
                case '(':                       
                    while(Ope.top() != ')'){  
                        n[ni ++] = Ope.top();  
                        n[ni ++] = ' ';  
                        Ope.pop();  
                    }  
                    Ope.pop();  
                    break;  
            }  

            if(s[i] != '('){                     
                Ope.push(s[i]);  
            }  
        }  
    }  

    while(n[ni - 1] == ' '){                   
        ni --;  
    }  
    while(!Ope.empty()){                         
        n[ni ++] = ' ';  
        n[ni ++] = Ope.top();  
        Ope.pop();  
    }  
    for(int i = ni - 1; i >= 0; i --){          
        printf("%c", n[i]);  
    }  
    printf(" =\n");  
}  

void To_Postfix(){           
    for(int i = 0; ; i ++){  
        if(isdigit(s[i]) || s[i] == '.'){         
            printf("%c", s[i]);  
        }  
        else{  
            if(i > 0 && isdigit(s[i - 1])){      
                printf(" ");  
            }  

            switch(s[i]){  
                case '(':                       
                    break;  
                case ')':                       
                    while(Ope.top() != '('){  
                        printf("%c ", Ope.top());  
                        Ope.pop();  
                    }  
                    Ope.pop();                  
                    break;  
                case '+':                       
                case '-':  
                case '=':  
                    while(!Ope.empty() && Ope.top() != '('){  
                        printf("%c ", Ope.top());  
                        Ope.pop();  
                    }  
                    break;  
                case '*':                        
                case '/':  
                    while(!Ope.empty() && (Ope.top() == '*' || Ope.top() == '/')){  
                        printf("%c ", Ope.top());  
                        Ope.pop();  
                    }  
                    break;        
            }  

            if(s[i] == '='){  
                printf("=\n");  
                break;  
            }  
            else{  
                if(s[i] != ')'){                 
                    Ope.push(s[i]);  
                }  
            }  
        }  
    }  
}  

int main(){  
    int loop;  
    scanf("%d", &loop);  
    while(loop --){  
        scanf(" %s", s);  

        To_Prefix();                
        To_Postfix();               

        double ans = JieXi();       
        printf("%.2lf\n", ans);  
    }  

    return 0;  
}  

double JieXi(){                
    int i = -1, ni = 0;           
    char num[100] = {"\0"};     
    double ans = 0;               

    while(1){  
        i ++;  

        if(isdigit(s[i]) || s[i] == '.'){                  
            num[ni ++] = s[i];  
        }  
        else{                                            
            if(ni != 0){                                   
                sscanf(num, "%lf", &ans);  
                Num.push(ans);  
                memset(num, '\0', sizeof(num));        
                ni = 0;  
            }  

            switch(s[i]){                                  
                case '+':  
                case '-':  
                case '=':  
                    while(!Ope.empty() && Ope.top() != '('){        
                        ans = JiSuan();  
                        Num.push(ans);                             
                    }  
                    break;  
                case '*':  
                case '/':  
                    while(!Ope.empty() && (Ope.top() == '*' || Ope.top() == '/')){  
                        ans = JiSuan();  
                        Num.push(ans);  
                    }  
                    break;  
                case ')':  
                    while(Ope.top() != '('){           
                        ans = JiSuan();  
                        Num.push(ans);  
                    }  
                    Ope.pop();                           
                    break;  
                case '(':                              
                    break;  
            }  

            if(s[i] == '='){  
                Num.pop();                      
                break;                            
            }  
            else{  
                if(s[i] != ')'){                
                    Ope.push(s[i]);  
                }  
            }  
        }  
    }  

    return ans;   
}  

double JiSuan(){                   
    double b = Num.top();  
    Num.pop();  
    double a = Num.top();  
    Num.pop();  
    char c = Ope.top();  
    Ope.pop();  

    switch(c){  
        case '+':  
            return a + b;  
        case '-':  
            return a - b;  
        case '*':  
            return a * b;  
        case '/':  
            return a / b;  
    }  
}

