void chushihua(){  
  sum[1]=1;a[1]=1;  
  for(int i=2;i<=9;++i){  
    sum[i]=sum[i-1]+a[1]+(i-1);  
    a[i]=sum[i]-sum[i-1];  
  }  
  for(int i=10;i<=99;++i){  
    sum[i]=sum[i-1]+a[9]+(i-9)*2;  
    a[i]=sum[i]-sum[i-1];  
  }  
  for(int i=100;i<=999;++i){  
    sum[i]=sum[i-1]+a[99]+(i-99)*3;  
    a[i]=sum[i]-sum[i-1];  
  }  
  for(int i=1000;i<=9999;++i){  
    sum[i]=sum[i-1]+a[999]+(i-999)*4;  
    a[i]=sum[i]-sum[i-1];  
  }  
  for(int i=10000;i<=34999;++i){  
    sum[i]=sum[i-1]+a[9999]+(i-9999)*5;  
    a[i]=sum[i]-sum[i-1];  
  }  
}  
