    sum+=n%100/10;
    sum+=n%1000/100;
    return sum;
}
int jz16(int n)
{
    int sum=0;
    while(n)
    {
        sum+=n%16;
        n/=16;
    }
    return sum;
}
int jz12(int n)
{
    int sum=0;
    while(n)
    {
        sum+=n%12;
        n/=12;
    }
    return sum;
}

