#include <iostream>
#include <cstdio>
#include <queue>

using namespace std;

const int inf = 100000;
const int maxn = 1005;

struct node{
	int v;
	int w;
	node *next;
	node(){
		v = 0; w = 0; next = NULL;
	}
};

struct qnode{
	int w;
	int index;
	qnode(int _w, int _index){
		w = _w; index = _index;
	}
	bool operator < (const qnode &b) const{
		return w > b.w;
	}
};

int n;
node* list[maxn];
int dist[maxn];
bool vis[maxn]; 
bool vis2[maxn]; 

void list_init(){
	for(int i = 0; i < maxn; i++)
		list[i] = new node;
}

void list_clear(){
	for(int i = 0; i <= n; i++){
		node *p = list[i];
		while(p->next){
		node *t = p->next;
		p->next = t->next;
		delete t;
		}
	}
}
bool input(){
	scanf("%d",&n);
	if(n == 0) return false;
	list_clear();
	int m;
	int v,w;
	for(int i = 1; i <= n; i++){
		scanf("%d",&m);
		node *p = list[i];
		for(int j = 0; j < m; j++){
			node *t = new node;
			scanf("%d%d",&t->v,&t->w);
			t->next = p->next;
			p->next =t;
		}
	}
	return true;
}
void dijkstra(int s){
	priority_queue<qnode> q;
	for(int i = 1; i <= n; i++){
		dist[i] = inf; vis[i] = false;
	}
	dist[s] = 0;
	q.push(qnode(0,s));
	while(!q.empty()){
		int u = q.top().index; q.pop();
		if(vis[u]) continue;
		vis[u] = true;
		node *p = list[u]->next;
		while(p){
			int v = p->v;
			int edge = p->w;
			if(!vis[v] && dist[u] + edge < dist[v]){
				dist[v] = dist[u] + edge;
				q.push(qnode(dist[v],v));
			}
			p = p->next;
		}
	}
}

void dfs(int s){
	vis2[s] = true;
	node *p = list[s]->next;
	while(p){
		int v = p->v;
		if(!vis2[v]){
			dfs(v);
		}
		p = p->next;
	}
}

void solve(){
	int minimum = inf;
	int pos;
	for(int i = 1; i <= n; i++){
		for(int j = 1; j <= n; j++) vis2[j] = false;
	dfs(i);
	bool fg = false;
	for(int j = 1; j <= n; j++) {
		if(vis2[j] == false) {
			fg = true; break;
		}
	}
	if(fg) continue;
	dijkstra(i);
	int maximum = -inf;
	for(int j = 1; j <= n; j++){
		if(dist[j] > maximum)
			maximum = dist[j];
		if(maximum == inf) break;
	}
	if(maximum != inf && maximum < minimum){
		minimum = maximum;
		pos = i;
	}
	}
	if(minimum == inf) puts("disjoint\n");
	else printf("%d %d\n",pos,minimum);
}

int main(){
	list_init();
	while(input()){
		solve();
	}
	return 0;
}

