using namespace std;  
int father[10005],num[10005],rankk[10005];  
int find(int x){  
  int fx=father[x];  
  if(fx==x){return father[x];}  
  else{  
    father[x]=find(father[x]);  
    rankk[x]+=rankk[fx];  
  }  
  return father[x];  
}  
int main(){  
    int numcase,k=0;  
    scanf("%d",&numcase);  
    while(numcase--){  
      int n,q;  
      scanf("%d%d",&n,&q);  
      for(int i=1;i<=n;++i)  
      {father[i]=i;num[i]=1;rankk[i]=0;}  
      char s[2];  
      int x,y;  
      printf("Case %d:\n",++k);  
      while(q--){  
          scanf("%s",s);  
          if(s[0]=='T'){  
          scanf("%d%d",&x,&y);  
          int rootx=find(x);  
          int rooty=find(y);  
          rankk[rootx]++;  
          father[rootx]=rooty;  
          num[rooty]+=num[rootx];  
        }  
        else{  
          scanf("%d",&x);  
          int rootx=find(x);  
          printf("%d %d %d\n",rootx,num[rootx],rankk[x]);  
        }  
      }  
    }  
