int find_t(int n) {  
    int p = 1, ret = 0;  
    while(true) {  
        ret++;  
        if(p % 2) p = n / 2 + (p + 1) / 2;  
        else p = p / 2;  

        if(p == 1) return ret;  
    }  
}  
int solve(int p, int n, int m) {  
    for(int i = 1; i <= m; i++) {  
        if(p * 2 <= n) p = p * 2;  
        else p = (p - n / 2 - 1) * 2 + 1;  
    }  
    return p;  
}  

