    int n, m, x; 
    while(~scanf("%d%d%d", &n, &m, &x)) {  
        int t = find_t(n);  

        for(int i = 1; i <= x; i++) {  
            printf("%d%c", solve(i, n, m % t), i == x ? '\n' : ' ');  
        }  
    }  
    return 0;  
}

