const int MAXN = 1024;  

LL dp[15][MAXN];  
int num, n, m;  

void DFS(int i, int j, int curstatus, int prestatus, int b1, int b2)  
{  
    if(j > m)  
    {  
        if(b1 == 0 && b2 == 0) 
            dp[i][curstatus] += dp[i-1][prestatus];  
        return ;  
    }  
    if(b1 == 0 && b2 == 0)
    {  
        DFS(i, j+1, curstatus<<1|1, prestatus<<1, 0, 0);
        DFS(i, j+1, curstatus<<1|1, prestatus<<1|1, 1, 0); 
        DFS(i, j+1, curstatus<<1|1, prestatus<<1, 1, 0);
        DFS(i, j+1, curstatus<<1|1, prestatus<<1, 0, 1);
        DFS(i, j+1, curstatus<<1, prestatus<<1, 1, 1);
        DFS(i, j+1, curstatus<<1|1, prestatus<<1|1, 1, 1);
        DFS(i, j+1, curstatus<<1, prestatus<<1|1, 0, 0);  
    }  

    if(b1 == 0 && b2 == 1) 
    {  
        DFS(i, j+1, curstatus<<1|1, prestatus<<1, 1, 0);  
        DFS(i, j+1, curstatus<<1|1, prestatus<<1, 1, 1);  
        DFS(i, j+1, curstatus<<1, prestatus<<1, 0, 0);
    }  

    if (b1 == 1 && b2 == 0)  
    {  
        DFS(i, j+1, curstatus<<1|1, prestatus<<1, 1, 1);
        DFS(i, j+1, curstatus<<1|1, prestatus<<1|1, 0, 0); 
    }  

    if (b1 == 1 && b2 == 1)  
    {  
        DFS(i, j+1, curstatus<<1|1, prestatus<<1, 0, 0);
    }  
    return ;  
}  

