        }  
        memset(dp, 0, sizeof(dp));  

        dp[0][(1<<m) -1] = 1;  
        for (int i = 1; i <= n; ++i)  
        {  
            DFS(i, 0, 0, 0, 0, 1);  
        }  
        printf("%lld\n", dp[n][(1<<m)-1]);  
    }  
