    for(int cas = 1; cas <= T; cas++)
    {
        scanf("%d %d", &m, &p);
        for(int i = 1; i <= m; i++)
            scanf("%d", &a[i]);
        printf("Case #%d:\n", cas);
        solve();
        putchar('\n');
    }
    return 0;
}

