void Fast_Euler()  
{  
    for(int i = 1; i < N; ++i) 
        euler[i] = i;  
    for(int i = 2; i < N; ++i)  
    {  
        if(euler[i] == i)  
        {  
            for(int j = i; j < N; j += i)  
                euler[j] = euler[j] / i * (i - 1);  
        }  
    }  
}  

