#include<stdio.h>
#include<string.h>
#include<iostream>
using namespace std;

long long key[40];
int fun(int n,int mid,int k,int f)
{
	if(n == mid)return 0;
	int sum = 0;
	while(mid && k){
		if(n == mid)
			break;
		else if(n < mid){
			mid -= key[k -1];
			sum = sum + f * (key[k - 1] - 1);
		}
		else{
			mid += key[k - 1];
			sum = sum - f * (key[k -1] - 1);
		}
		k --;
	}
	return sum;
}

int main()
{
	key[0] = 1;
	for(int i = 1;i < 33; ++ i)
		key[i] = key[i - 1] * 2;
	long long n, m, temp;
	while(~scanf("%lld%lld", &n, &m))
	{
		int k;
		if(n > m)temp = n,n = m,m =temp;
		for(int i = 0;i < 32; ++ i)
			if(key[i] >= m){
				k = i;
				break;
			}
		int mid = key[k];

		while(mid){
			if(n <= mid && m >= mid)
				break;
			else if(m < mid)
				mid -= key[k - 1];
			else
				mid += key[k - 1];
			k --;
		}
		printf("%d\n", fun(n, mid, k, 1) + fun(m, mid, k, -1));
	}
}

#include<iostream>  
