using namespace std;  
struct node{  
    int x,y,step,time;  
};  
int dir[4][2]={1,0,0,1,-1,0,0,-1},map[12][12];  
bool ever[12][12];  
int BFS(int x,int y){  
    node cur,next;  
    cur.x = x;cur.y = y;cur.step = 0,cur.time = 6;  
    queue<node>q;  
    q.push(cur);  
    while(!q.empty()){  
        cur = q.front();  
        q.pop();  
        if(map[cur.x][cur.y]==3){  
            return cur.step;  
        }  
        if(cur.time == 1) continue;  
        for(int i=0;i<4;++i){  
            next.x = cur.x + dir[i][0];  
            next.y = cur.y + dir[i][1];  
            next.step = cur.step + 1;  
            next.time = cur.time - 1;  
            if(map[next.x][next.y]&&!ever[next.x][next.y]){  
                if(map[next.x][next.y] == 4){  
                    next.time = 6;  
                    map[next.x][next.y] = 1;  
                }  
                q.push(next);  
            }  
        }  
    }  
    return -1;  
}  
int main(){  
    int n,m,T,startx=1,starty=1;  
    scanf("%d",&T);  
    while(T--){   
        memset(map,0,sizeof(map));  
        scanf("%d%d",&n,&m);  
        for(int i=1;i<=n;++i){  
            for(int j=1;j<=m;++j){  
                scanf("%d",&map[i][j]);  
                if(map[i][j]==2) startx = i,starty = j;  
            }  
        }  
         printf("%d\n",BFS(startx,starty));  

    }  
    return 0;  
}

