bool ok(int x){  
    if(x&(x<<1)) return false;  
    return true;  
}  
void init(){  
    cnt = 0;  
    for(int i = 0;i < (1<<n);i++){  
        if(ok(i)) s[cnt++] = i;  
    }  
}  
int cal(int x){  
    int count = 0;  
    while(x){  
        count ++;  
        x &= (x-1);  
    }  
    return count;  
}  
int main(){  
    while(cin >> n >> K){  
        init();  
        memset(dp,0,sizeof(dp));  
        for(int i = 0;i < cnt;i++){  
            p[i] = cal(s[i]);  
            if(p[i] <= K) dp[0][i][p[i]] = 1;  
        }  
        for(int i = 1;i < n;i++){  
            for(int j = 0;j < cnt;j++){  
                if(p[j] > K) continue;  
                for(int k = 0;k < cnt;k++){  
                    if(s[j]&s[k]) continue;  
                    if((s[j]&(s[k]<<1))||(s[j]&(s[k]>>1))) continue;  
                    for(int r = 0;r <= K;r++)  
                        if(r + p[j] <= K )  
                            dp[i][j][r+p[j]] += dp[i-1][k][r];  
                }  
            }  
        }  
        LL ans = 0;  
        for(int i = 0;i < cnt;i++){  
            ans += dp[n-1][i][K];  
        }  
        cout << ans << endl;  
    }  
}

