int judge(int buf[], int n, int s, int p)
{
	int i, a[100], b[100], cnt = 0;
	for (i = 0; i < n; i++) {
	if (buf[i] == 0 || buf[i] == 1)
	a[i] = buf[i], b[i] = -1;
	else if (buf[i] == 29 || buf[i] == 30)
	a[i] = 10, b[i] = -1;
	else if (buf[i] >= 2 && buf[i] <= 28)
	if (buf[i] % 3 == 2)
	a[i] = buf[i] / 3 + 1, b[i] = buf[i] / 3 + 2;
	else
	a[i] = buf[i] / 3 + buf[i] % 3, b[i] = buf[i] / 3 + 1;
	}
	for (i = 0; i < n && cnt < s; i++)
		if (b[i] >= p)
			a[i] = -1, cnt++;
	for (i = 0; i < n; i++)
		if (a[i] >= p) cnt++;
			return cnt;
}

void bubble(int a[], int n)
{
	int i, j, tmp, swapped = 1;
	for (i = 0; swapped; i++) {
		swapped = 0;
		for (j = 0; j < n - (i + 1); j++) {
			if (a[j] > a[j + 1]) {
				tmp = a[j];
				a[j] = a[j + 1];
				a[j + 1] = tmp;
				swapped = 1;
			}
		}
	}
}

