void creat(int pre1,int in1,int n,bt &b)  
{  
    int p;  
    int k;  
    if(n<=0)  
    {  
        b=NULL;  
        return;  
    }  
    b=(bt)malloc(sizeof(struct tnode));  
    b->data=pre[pre1];  
    for(p=in1; p<in1+n; p++)  
    {  
        if(pre[pre1]==in[p])  
        {  
            break;  
        }  
    }  
    k=p-in1;  
    creat(pre1+1,in1,k,b->lchild);  
    creat(pre1+k+1,p+1,n-k-1,b->rchild);  
}  
