{  
    if(b==NULL)  
    {  
        return ;  
    }  
    if(b->data==x)  
    {  
        pre_vis(b);  
        return;  
    }  
    else  
    {  
        findbt(b->lchild,x);  
        findbt(b->rchild,x);  
    }  
}  
