double f(double x)  
{  
    return 8*pow(x,4) - 7*pow(x,3) + 2*pow(x,2) + 3*x + 6;  
}  
double Binary_Find(double left,double right,double y)  
{  
    while(right-left>1e-7)  
    {  
        double mid=(left+right)/2;  
        if(f(mid) < y)  
            left  = mid;  
        else  
            right = mid;  
    }  
    return left;  
}  
