int Matrix::Pow(int n)   
{   Matrix temp(4,4),Start(4,1);  
    temp.Init();  
    Start.Clear();  
    while(n)  
    {   if(n&1) M=M*temp;  
        temp=temp*temp;  
        n>>=1;  
    }  
    Start=M*Start;  
    return Start.Result();      
}  
