int qmin(int x,int y)
{
   return ((x)<(y))?(x):(y);
} 

int dij(int N)
{
   int i,j;
   for(i=1;i<=N;++i)
    d[i]=price[0][i];
   for(i=1;i<=N;++i)
    {
       int mi=inf;
       int k=0;
       for(j=1;j<=N;++j)
        if(!flag[j] && mi>d[j])
        {
          mi=d[j];
          k=j;
        } 
       if(!k)break;
       flag[k]=true;

       for(j=1;j<=N;++j)
        {
          if(!flag[j] &&  d[j] > d[k]+price[k][j])
           {
              d[j]=d[k]+price[k][j];
           }
        }
    }
   return d[1];
}

