    {
      for(i=1;i<=N;++i)
       {
          price[i][i]=0;
          for(j=i+1;j<=N;++j)
           price[i][j]=price[j][i]=inf;
       } 
      for(i=1;i<=N;++i)
       {
         scanf("%d%d%d",&price[0][i],&level[i],&X);
         for(j=0;j<X;++j)
          {
            scanf("%d%d",&a,&b);
            price[a][i]=b;
          }
       }

      int maxlevel;
