    for(i=0;prime[i]<=(int)sqrt(n*1.0);++i){  
        if(n==1)break;  
        if(n%prime[i]==0){  
            while(n%prime[i]==0){  
              n/=prime[i];  
            }  
        }  
    }  
    if(n==1)  
    return prime[i-1];  
    else return n;  
