}

#include <stdio.h>  
#include <ctype.h>  
#include <string.h>  
#include <stdlib.h>  
const int M=100000;  
void back(char* c,int &l)  
{  
    if(strchr(c,'.') == NULL)           
        return ;  
    for(int i=l-1;i>=1;i--)            
    {  
        if(c[i]!='0')  
            break;  
        l--;  
    }  
    if(c[l-1]=='.')  
        l--;  
    c[l]='\0';  
}  
void front(char* c,int &l,bool &f)  
{  
    int cnt = isdigit(c[0]) ? 0 : 1;   
    if(c[0]=='-')  
        f = false;  
    for(int i=cnt;i<l-1;i++)            
    {  
        if(c[i+1]=='.' || c[i]!='0')    
            break;  
        cnt++;  
    }  
    if(cnt)  
    {  
        for(int i=0;i<l-cnt;i++)        
            c[i] = c[i+cnt];  
        c[l-cnt] = '\0';  
    }  
}  
void deal(char *c,bool &f)  
{  
    int l = strlen(c);  
    f = true;                          
    back(c,l);  
    front(c,l,f);  
}  
bool Cmp(char* A,char* B,bool a,bool b)  
{  
    if(strcmp(A,B) == 0)  
    {  
        if(strcmp(A,"0") == 0 || a^b == 0)  
            return true;  
    }  
    return false;  
}  
int main()  
{  
    char A[M],B[M];  
    bool a,b;  
    while(~scanf("%s%s",A,B))  
    {  
        deal(A,a);  
        deal(B,b);  
        puts(Cmp(A,B,a,b)?"YES":"NO");  
    }  

    return 0;  
}

