#define maxn 11111  
#define inf 11111111  
struct edge{int to,cap,rev;  
    edge(int to=0,int cap=0,int rev=0):to(to),cap(cap),rev(rev){}  
};  
vector<edge>g[maxn];  
int lv[maxn],iter[maxn];  
void add(int u,int v,int w)  
{  
    edge a;a.to=v;a.cap=w;a.rev=(int)g[v].size();  
    g[u].push_back(a);  
    a.to=u;a.cap=0;a.rev=(int)g[u].size()-1;  
    g[v].push_back(a);  
}  
void bfs(int s)  
{  
    memset(lv,-1,sizeof lv);  
    queue<int>q;  
    q.push(s);  
    lv[s]=0;  
    int v;  
    while(!q.empty())  
    {  
        v=q.front();  
        q.pop();  
        for(int i=0;i<g[v].size();++i)  
        {  
            if(lv[g[v][i].to]<0&&g[v][i].cap>0)  
            {  
                lv[g[v][i].to]=lv[v]+1;  
                q.push(g[v][i].to);  
            }  
        }  
    }  
}  
int dfs(int u,int v,int f)  
{  
    if(u==v)return f;  
    for(int &i=iter[u];i<g[u].size();++i)  
    {  
        edge &e=g[u][i];  
        if(lv[u]<lv[e.to]&&e.cap>0)
        {  
            int d=dfs(e.to,v,min(f,e.cap));  
            if(d>0)  
            {  
                e.cap-=d;  
                g[e.to][e.rev].cap+=d;  
                return d;  
            }  
        }  
    }  
    return 0;  
}  
int max_flow(int s,int t)  
{  
    int flow=0;  
    for(;;)  
    {  
        memset(lv,-1,sizeof lv);  
        bfs(s);  
        if(lv[t]<0)return flow;  
        memset(iter,0,sizeof iter);    
        int f;  
        while((f=dfs(s,t,inf))>0){  
            flow+=f;  
        }  
    }  
}  
