#include <iostream>  
#include <cstdio>  
#include <string.h>  
using namespace std;  
const int N=50010;  
#define max(x,y) ((x)>(y)?(x):(y))  
struct tree{  
    int left,right,flag;  
    int lsum,rsum,sum;  
    int getmid(){  
      return (left+right)/2;  
    }  
    void change(int pos);  
}tt[N*4];  
void tree::change(int pos){  
       tt[pos*2].flag=tt[pos*2+1].flag=tt[pos].flag;  
        int x,y;  
        if(tt[pos].flag){  
          x=y=0;  
        }  
        else{  
          x=tt[pos*2].right-tt[pos*2].left+1;  
          y=tt[pos*2+1].right-tt[pos*2+1].left+1;  
        }  
        tt[pos*2].lsum=tt[pos*2].rsum=tt[pos*2].sum=x;  
        tt[pos*2+1].lsum=tt[pos*2+1].rsum=tt[pos*2+1].sum=y;  
        tt[pos].flag=-1;  
}  
void built_tree(int lp,int rp,int pos){  
    tt[pos].left=lp;  
    tt[pos].right=rp;  
    tt[pos].flag=0;  
    tt[pos].lsum=tt[pos].rsum=tt[pos].sum=rp-lp+1;  
    if(lp==rp)return;  
    int mid=tt[pos].getmid();  
    built_tree(lp,mid,pos*2);  
    built_tree(mid+1,rp,pos*2+1);  
}  
int find(int x,int pos){  
    if(tt[pos].right==tt[pos].left){  
      return tt[pos].left;  
    }  
    if(tt[pos].flag!=-1){  
      tt[pos].change(pos);  
    }  
    if(tt[pos*2].sum>=x){  
      return find(x,pos*2);  
    }  
    else if(tt[pos*2].rsum+tt[pos*2+1].lsum>=x){  
      return tt[pos*2].right-tt[pos*2].rsum+1;  
    }  
    else if(tt[pos*2+1].sum>=x){  
      return find(x,pos*2+1);  
    }  
    else{  
      return 0;  
    }  
}  
void update(int lp,int rp,int modl,int pos){  
    if(tt[pos].left==lp&&tt[pos].right==rp){  
        if(modl==1){  
          tt[pos].lsum=tt[pos].rsum=tt[pos].sum=0;  
        }  
        else{  
            tt[pos].lsum=tt[pos].rsum=tt[pos].sum=tt[pos].right-tt[pos].left+1;  
        }  
        tt[pos].flag=modl;  
        return;  
    }  
    if(tt[pos].flag!=-1){  
      tt[pos].change(pos);  
    }  
    int mid=tt[pos].getmid();  
    if(rp<=mid)  
        update(lp,rp,modl,pos*2);  
    else if(lp>mid)  
        update(lp,rp,modl,pos*2+1);  
    else{  
      update(lp,mid,modl,pos*2);  
      update(mid+1,rp,modl,pos*2+1);  
    }  
    tt[pos].sum=max(max(tt[pos*2].sum,tt[pos*2+1].sum),tt[pos*2].rsum+tt[pos*2+1].lsum);  
    tt[pos].lsum=tt[pos*2].lsum;  
    tt[pos].rsum=tt[pos*2+1].rsum;  
    if(tt[pos*2].lsum==tt[pos*2].right-tt[pos*2].left+1)  
        tt[pos].lsum+=tt[pos*2+1].lsum;  
    if(tt[pos*2+1].rsum==tt[pos*2+1].right-tt[pos*2+1].left+1)  
        tt[pos].rsum+=tt[pos*2].rsum;  
}  
int main(){  
    int n,m;  
    while(~scanf("%d%d",&n,&m)){  
      built_tree(1,n,1);  
      int mod,x,y,ans;  
      while(m--){  
        scanf("%d",&mod);  
        if(mod==1){  
          scanf("%d",&x);  
          ans=0;  
          ans=find(x,1);  
          printf("%d\n",ans);  
          if(ans){  
            update(ans,ans+x-1,1,1);  
          }  
        }  
        else{  
          scanf("%d%d",&x,&y);  
          update(x,x+y-1,0,1);  
        }  
      }  
    }  
    return 0;  
}

