char *strrev(char *str)
{  
    int len=strlen(str);  
    char *begin=str;  
    char *end=str+len-1;  
    while(begin<end)  
    {  
        char temp;  
        temp=*begin;  
        *begin=*end;  
        *end=temp;  
        ++begin;  
        --end;  
    }  
    return str;  
}  
