int p[N][N];
struct node {
  int x,y,w;
  friend bool operator<(node a,node b) {
   return a.w>b.w;
  }
};
int visit[N][N];
int dis[4][2]={1,0,0,1,-1,0,0,-1};
int judge(int x,int y) {
if(x<=n&&x>=1&&y<=m&&y>=1&&!visit[x][y])
    return 1;
return 0;
}
int main() {
    int i,j,sum;
while(scanf("%d%d",&m,&n)!=EOF) {
        priority_queue<node>q;
        node cur,next;
        memset(visit,0,sizeof(visit));
    for(i=1;i<=n;i++)
        for(j=1;j<=m;j++) {
        scanf("%d",&p[i][j]);
        if(j==1||j==m||i==1||i==n) {
        cur.x=i;cur.y=j;
        cur.w=p[i][j];
        visit[i][j]=1;
        q.push(cur);
        }
        }
        sum=0;
        while(!q.empty()) {
            cur=q.top();
            q.pop();
            for(i=0;i<4;i++) {
                int xx=next.x=cur.x+dis[i][0];
                int yy=next.y=cur.y+dis[i][1];
                if(judge(xx,yy)) {
                        visit[xx][yy]=1;
                    if(p[xx][yy]<cur.w) {
                        sum+=cur.w-p[xx][yy];
                        next.w=cur.w;
                        q.push(next);
                    }
                    else {
                        next.w=p[xx][yy];
                        q.push(next);
                    }
                }
            }
        }
        printf("%d\n",sum);
    }
return 0;
}

#include <stdio.h>  
#include <math.h>  
#define pi 3.14159265358  
double rest(double a[],int n,double x0,double xt)  
{  
    int i;  
    double sum=0.0;  
    for(i=0;i<=n;i++)  
        sum+=(pow(xt,i+1)-pow(x0,i+1))*a[i]/(i+1.0);  
    return sum*pi;  
}  
double br(double a[],int n,double x0,double st,double xt,double v)  
{  
    double mid=(xt+st)*0.5;  
    if(fabs(rest(a,n,x0,mid)-v)>0.001)  
        if(rest(a,n,x0,mid)-v>0)  
            return br(a,n,x0,st,mid,v);  
        else  
            return br(a,n,x0,mid,xt,v);   
    else  
        return mid;  
}  
int main()  
{  
