        for(i=0;i<=n;i++)  
            scanf("%lf",&p1[i]);  
        for(i=0;i<30;i++)  
            p[i]=0.0;  
        for(i=0;i<=n;i++)  
            for(j=0;j<=n;j++)  
                p[i+j]+=p1[i]*p1[j];  
        scanf("%lf%lf%lf",&low,&high,&bound);  
        printf("Case %d: %.2lf\n",++m,st=rest(p,2*n,low,high));  
        if(st<bound)  
            puts("insufficient volume");  
        else  
        {  
            st=low;  
            for(i=0;i<8;i++)  
            {  
                if(rest(p,2*n,low,high)<bound)  
                    break;  
                printf("%.2lf ",(low=br(p,2*n,low,low,high,bound))-st);  
            }  
            printf("\n");  
        }  
    }  
}

